/**
 *  Pollyfill masking
 */
Array.prototype.random = function () {
  return this[Math.floor((Math.random()*this.length))];
}

/**
 *  Execution Thread
 */
function UpdateLayout() {
    var Height = $(window).height(),
        Width = $(window).width(),
        SidebarHeight = 0;

    $('.sidebar').children().not('.chat-container').each(function() {
        SidebarHeight += $(this).outerHeight();
    });

    if ($('.sidebar').height() < 580) {
        $('.sidebar').css('overflow-y', 'scroll').perfectScrollbar();
        $('.sidebar .chat-messages, .sidebar .chat-input').slideUp();
        $('.sidebar .chat-mobile-alert').slideDown();
        $('.sidebar .fade-area').css('display', 'none');
    } else if ($('.sidebar').height() > 580) {
        $('.sidebar').css('overflow-y', 'hidden').perfectScrollbar('destroy');
        $('.sidebar .chat-messages, .sidebar .chat-input').slideDown();
        $('.sidebar .chat-mobile-alert').slideUp();
        $('.sidebar .fade-area').css('display', 'block');
        $('.sidebar .chat-container .messages').css('height', $('.sidebar').height() - SidebarHeight - 34);
    }
}

var Socket,
    //Sound = new Audio("data:audio/wav;base64,"),
    ChatID = 0,
    User = {
        Name: '',
        IMG: 'https://steamrep.com/steamimage/avatars/68/68929ec41da387112252c6b79f89ab18d5cb956e_full.jpg',
        Rank: ['0','1','2','3'].random(),
        SteamID: Math.random().toString(36).substr(2, 10)
    };

$(function(){
    UpdateLayout();
    
    User.Name = prompt('Name: ');
    if(User.Name === null) User.Name = 'Anonymoose';
    
    Socket = io.connect('https://dev.steamtrade.io:8888', {
        reconnect: true
    });
    
    Socket.on('connect_error', function(){
        Template = '';
        Template += '<div class="chat-message ' + (ChatID % 2 === 1 ? 'odd' : 'even') + '">';
        Template += '    <img src="" class="pp" />';
        Template += '    <div class="data">';
        Template += '        <span class="name rank-2" data-id="333" data-steamid="333">ALERT!</span>';
        Template += '        <div class="flair rank-2">Developer</div>';
        Template += '        <p class="message">Cannot connect to socket!</p>';
        Template += '    </div>';
        Template += '</div>';

        ChatID++;
        
        $('.messages').append(Template);
    });

    Socket.on('ID', function(Data){
        User.ID = Data.ID;
    });

    Socket.on('ReceiveMessage', function(Data){
        Template = '';

                            Template += '<div class="chat-message ' + (ChatID % 2 === 1 ? 'odd' : 'even') + '">';
                            Template += '    <img src="' + Data.IMG + '" class="pp" />';
                            Template += '    <div class="data">';
                            Template += '        <span class="name rank-' + Data.Rank + '" data-id="' + Data.ID + '" data-steamid="">' + Data.Name + '</span>';
        if(Data.Rank > 0)   Template += '        <div class="flair rank-' + Data.Rank + '">' + ['ADMIN','DEVELOPER','TWITCH'][Data.Rank - 1] + '</div>';
                            Template += '        <p class="message">' + Data.Message + '</p>';
                            Template += '    </div>';
                            Template += '</div>';

        ChatID++;
        
        $('.messages').append(Template);
        $('.sidebar .chat-container .messages').scrollTo($('.chat-message').children().last());
        
        if(Sound) Sound.play();

        if ($('.sidebar .chat-container .messages').children().length > 25) $('.sidebar .chat-container .messages').children().first().remove();
    });
    
    $('#chatInput').keypress(function(e){
        if(e.which == 13 && typeof User.ID !== 'undefined' && $('#chatInput').val() !== ''){
            Socket.emit('SendMessage', {
                'Name': User.Name,
                'Message': $('#chatInput').val(),
                'IMG': User.IMG,
                'Rank': User.Rank,
                'ID': User.ID,
                'SteamID': User.SteamID
            });
            $('#chatInput').val('');
        }
    });
});
